body.script:
(function(w, SA) {

  'use strict';

  var mods = SA.modules, reqs = SA.requires, log = /salog/.test(location.hash);

  function nSpace(ns, fn, force) {

    // jshint singleGroups: false

    var names = ns.split('.'), s = window.SA, l;

    if (names[0] === 'SA') {
      names = names.slice(1);
    }

    l = names.length;

    names.forEach(function(name, i) {
      if (s[name] === undefined || force) {
        s[name] = (i === l - 1 && fn && typeof fn === 'function') ? fn() : {};
      }
      s = s[name];
    });

    return s;
  }

  function clearResolved(deps) {

    // jshint singleGroups: false

    var l = [];
    deps = (deps && Array.isArray(deps)) ? deps : [];
    deps.forEach(function(d) {
      if(!mods[d] || !mods[d].resolved) {
        l.push(d);
      }
    });
    return l;
  }

  function scope(deps) {
    var res = [];
    deps.forEach(function(d) {
      res.push(nSpace(d));
    });
    return res;
  }

  function reqExec(f, deps, n) {
    SA.run(function() {
      if(typeof f === 'function') {
        SA.log('RESOLVED: ' + n);
        return f.apply(SA, scope(deps));
      }
    });
  }

  function delayRes(f, deps, n) {
    SA.log('WAITING RESOLUTION ' + n);
    return reqs.push([f, deps, n]);
  }

  function resolveReq() {
    var req = [];
    reqs.forEach(function(r){
      if(!clearResolved(r[1]).length) {
        reqExec(r[0], r[1], r[2]);
      } else {
        req.push(r);
      }
    });
    reqs = req;
  }

  function resolve(name, deps, fn) {
    SA.log('DEFINE: ' + name + '. Deps: ' + deps);
    var depsLeft = clearResolved(deps);
    mods[name] = {resolved: !depsLeft.length};
    if(mods[name].resolved) {
      SA.run(function() {
        nSpace(name, function() {
          return fn.apply(window.SA, scope(deps));
        });
        SA.log('RESOLVED: ' + name);
        resolveReq();
      });
      Object.keys(mods).forEach(function(k) {
        if(!mods[k].resolved && k !== name) {
          resolve(k, mods[k].deps, mods[k].fn);
        }
      });
    } else {
      SA.log('WAITING RESOLUTION ' + name);
      mods[name].deps = deps;
      mods[name].fn = fn;
    }
  }


  // exception handler. Executes provided function and sends custom message to Rollbar in case of exception
  // @param {function} f - function to execute
  // @param {string} m - message to send in case of execution failure (optional)
  // Usage:
  //   var div = SA.run(function() {
  //     SA.Author.show();
  //   }, 'author scope not defined')

  SA.run = function(f, m) {
    var res;
    try {
      res = f();
    } catch(e) {
      w.console.error(e);
      SA.exceptions.push([m || e.message || 'SA execution error', e, {}]);
    }
    return res;
  };

  // exception handler. Executes provided function and silently catches exception in case there was one
  // @param {function} f - function to execute
  // Usage:
  //   SA.exec(function() {
  //     SA.Author.show();
  //   })

  SA.exec = function(f) {
    var res;
    try {
      res = f();
    } catch(ignore) {}
    return res;
  };

  SA.execIfResolved = function(deps, f) {
    if(!clearResolved(deps).length) {
      reqExec(f, deps, 'EXEC-IF: ' + f.toString());
    }
  };

  // a convenience function for parsing string namespaces and
  // automatically generating nested namespaces
  // sample usage:
  // var scope = SA.UsingNamespace('SA.Widget.Test');
  // @param {string} ns - dot separated desired string namespace.

  // @return {Object} - nested object namespace.
  SA.UsingNamespace = function(ns, fn) {
    return nSpace(ns, fn);
  };

  SA.log = function(m) {
    if(log) {
      w.console.warn('%c SA-LOG: ', 'background: #1a9afd; color: white', m) ;
    }
  };

  SA.define = function(name, deps, fn) {
    if(!mods[name]) {
      resolve(name, deps, fn);
    }
  };

  SA.forceResolve = function(name, fn) {
    nSpace(name, fn);
    mods[name] = {
      resolved: true
    };
  };

  SA.require = function(deps, f, n) {
    SA.log('REQUIRE: ' + n + '. Deps: ' + deps);
    return clearResolved(deps).length ? delayRes(f, deps, n) : reqExec(f, deps, n);
  };

}(window, window.SA));

SA.define('SA.Fn.param', [], function() {

  'use strict';

  return function(name, url) {
    var regex, results;
    url = url || window.location.href;
    name = name.replace(/[\[\]]/g, '\\$&');
    regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)');
    results = regex.exec(url);
    if (!results) {
      return null;
    }
    if (!results[2]) {
      return '';
    }
    return decodeURIComponent(results[2].replace(/\+/g, ' '));
  };

});
SA.define('SA.Fn.ajax', [], function () {

  'use strict';

  return function (url, clb, opt) {
    var req = new XMLHttpRequest();
    opt = opt || {};
    opt.fail = opt.fail || function () { return false; };
    clb = clb || function () {};

    req.open(opt.method || 'GET', url, true);
    req.setRequestHeader('X-REQUESTED-WITH', 'XMLHttpRequest');
    req.onreadystatechange = function () {
      if (this.readyState === 4) {
        return (this.status >= 200 && this.status < 400) ? clb(opt.txt ? this.responseText : JSON.parse(this.responseText)) : opt.fail();
      }
    };
    req.send();
    req = null;
  };
});
