SA.define('SA.pk', [], function() {

  'use strict';

  var pk;

  function gen() {
    var k = Math.floor(Date.now() / 1000) * 1000 + Math.floor(Math.random() * 1000);
    pk = k.toString(36);
    return pk;
  }

  gen();

  return {
    generate: function() {
      return gen();
    },
    get: function() {
      return pk;
    },
    set: function(newPk) {
      pk = newPk;
    }
  };

});
