SA.define('SA.loadJS', [], function() {

  'use strict';

  var d = document, h = d.getElementsByTagName('head')[0];

  function ready(url) {
    return function () {
      var rs = this.readyState;
      if (rs && rs !== 'complete' && rs !== 'loaded') {
        return;
      }
      url.loaded = true;
      SA.log('LOADED: ' + url.src);
      if(url.callback) {
        url.callback();
      }
    };
  }

  function er(url) {
    return function() {
      SA.log('LOAD-ERROR: ' + url.src);
      if(url.error) {
        url.error();
      }
    };
  }

  return function(urls) {
    var s, gptHeaderTest;
    urls.forEach(function(url) {
      gptHeaderTest = url.id === 'gpt' && SA.App.gptHeaderTest;
      if(url.src && !gptHeaderTest) {
        s = d.createElement('script');
        s.async = true;
        s.type = 'text/javascript';
        s.src = url.src.replace('{{rnd}}', Math.floor(Math.random() * 99999999999));
        s.onload = ready(url);
        s.onreadystatechange = ready(url);
        s.onerror = er(url);
        if (url.id) {
          s.id = url.id;
        }
        h.appendChild(s);
        SA.log('REQUEST: ' + url.src);
      }
    });
  };

});
SA.define('SA.inlineCSS', [], function() {

  'use strict';

  var d = document, h = d.getElementsByTagName('head')[0], m = {};

  return function(css, mod) {
    var s;
    if(!m[mod] && css) {
      s = d.createElement('style');
      s.textContent = css;
      s.setAttribute('sa-mod', mod);
      m[mod] = s;
      h.appendChild(s);
    }
  };

});
SA.define('SA.readCookie', [], function() {

  'use strict';

  var d = document;

  return function(n) {

    return SA.exec(function() {
      var c = d.cookie.match(new RegExp('(^|;)\\s*' + escape(n) + '=([^;\\s]*)'));
      return c ? unescape(c[2]) : null;
    });

  };

});
