SA.require(['SA.readCookie', 'SA.Fn.param', 'SA.AdsConf'], function (rc, param, adsConf) {

  'use strict';

  var w = window, d = document, s = SA.pageConfig.saSource || param('source') || rc('_sasource'), e;

  function isOTM() {
    return (SA.pageConfig && SA.pageConfig.Data &&
      SA.pageConfig.Data.mc && SA.pageConfig.Data.mc.tags &&
      SA.pageConfig.Data.mc.tags.indexOf('on-the-move') > -1
    );
  }

  function isSsga() {
    return location.href.match(/\/market-outlook\/gold-and-precious-metals/);
  }

  function fromEvtImpact() {
    return /\/event-impact\/?$/.test(document.referrer);
  }

  function isNuveenCef() {
    return location.href.match(/\/etfs-and-funds\/closed-end-funds/);
  }

  function isIshares() {
    return (location.href.match(/\/etfs-and-funds\/etf-screener/) || (SA.pageConfig &&
      SA.pageConfig.Data &&
      SA.pageConfig.Data.isIshares));
  }

  function bb() {
      var notBranded = !isOTM() && !isSsga() && !isNuveenCef() && !isIshares() && !fromEvtImpact(),
      isAdsEnvironment = adsConf.isUSA || (/^test\./).test(w.location.host),
      notAdsFree = !(SA.UserCookie || {}).ad_free_user,
      inDebugMode = !!param('krux_bb'),
      fromMobileWeb = !!rc('show_desktop');
    if (((isAdsEnvironment && notAdsFree) || inDebugMode) && notBranded && !fromMobileWeb) {
      e = d.querySelectorAll('#krux-container .' + (s === 'refreshed' ? 'nonrefresh' : 'refresh'))[0];
      if (e) {
        e.parentNode.removeChild(e);
      }
      e = document.getElementById('krux-container');
      if (e) {
        e.style.display = '';
      }
    } else {
      e = document.getElementById('krux-container');
      if (e) {
        e.parentNode.removeChild(e);
      }
    }
  }

  // Adjust broker buttons
  bb();

}, 'adjust-bb');
